function brPhoneFormatter(){return function(r){if(!r||"string"!=typeof r)return"";r=r.replace(/[^0-9.]/g,"");var t=r.length;return 6>t?r:7>t?"("+r.substring(0,2)+") "+r.substring(2,t):9===t?"("+r.substring(0,2)+") "+r.substring(2,5)+"-"+r.substring(5,t):"("+r.substring(0,2)+") "+r.substring(2,6)+"-"+r.substring(6,t)}}angular.module("br-formatter",[]),angular.module("br-formatter").filter("brPhoneFormatter",brPhoneFormatter);